import { FunctionComponent } from 'react';
import { ITarget } from './Helpers';

export type ICheckboxTarget = ITarget & { checked: boolean };

export interface ICheckboxProps {
  name: string;
  label: string;
  value?: string;
  checked?: boolean;
  disabled?: boolean;
  className?: string;
  onChange: (target: ICheckboxTarget) => void;
}

declare const Checkbox: FunctionComponent<ICheckboxProps>;

export default Checkbox;
