import { PureComponent } from 'react';

export interface IPage {
  links?: {
    first?: {
      href: string;
    };
    last?: {
      href: string;
    };
    next?: {
      href: string;
    };
    prev?: {
      href: string;
    };
    self: {
      href: string;
    };
  };
  number?: number;
  size?: number;
  totalElements?: number;
  totalPages?: number;
}

export interface IPaginationProps {
  checkedPageSize?: number;
  className?: string;
  page: IPage;
  hideOnSinglePage?: boolean;
  isAsync: boolean;
  pageSizes?: number[];
  onSelect: (url: string | number) => Promise<void>;
  onPageSizeChange?: (pageSize: number) => void;
}

declare class Pagination extends PureComponent<IPaginationProps> {}

export default Pagination;
