import { PureComponent } from 'react';
import { IOption, ITarget } from './Helpers';

export type ISearchDropDownTarget = ITarget;

export interface ISearchDropDownOption extends IOption {
  metadata?: string;
  isDisabled?: boolean;
}

export interface ISearchDropDownProps {
  className?: string;
  disabled?: boolean;
  hasTitleAttribute?: boolean;
  id?: string;
  isCleanedValues?: boolean;
  maxResults?: number;
  name: string;
  onChange?: (target: ISearchDropDownTarget) => void;
  onSearch: (value: string) => void;
  onSelect?: (option: ISearchDropDownOption) => void;
  options: ISearchDropDownOption[];
  placeholder?: string;
  selectedOption?: ISearchDropDownOption;
}

declare class SearchDropDown extends PureComponent<ISearchDropDownProps> {}

export default SearchDropDown;
