import { PureComponent } from 'react';
import { IOption, ITarget } from './Helpers';

export type ISelectOption = IOption;

export type ISelectTarget = ITarget;

export interface ISelectProps {
  className?: string;
  isCleanedValue?: boolean;
  onChange: (target: ISelectTarget) => void;
  options: ISelectOption[];
  name: string;
  placeholder?: string;
  value?: string;
  fullWidth?: boolean;
  disabled?: boolean;
}

declare class Select extends PureComponent<ISelectProps> {}

export default Select;
