import { PureComponent, ReactElement } from 'react';

export interface IRenderRowParams<Item> {
  item: Item;
  itemSchema: ISchema<Item>[];
  itemKey: string;
  className?: string;
  rowHeightSize?: string;
}

export type IRenderRow<Item> = ({
  item,
  itemSchema,
  itemKey,
  className,
  rowHeightSize
}: IRenderRowParams<Item>) => ReactElement;

export interface IFirstSchema<Item> {
  attributes: string[];
  className?: string;
  headerTemplate?: (field: ISchema<Item>) => ReactElement;
  name: string;
}

export interface ISecondSchema<Item> {
  className?: string;
  headerTemplate?: (field: ISchema<Item>) => ReactElement;
  template: (field: Item) => ReactElement | string;
  name: string;
}

export type ISchema<Item> = IFirstSchema<Item> | ISecondSchema<Item>;

export interface ITableProps<Item> {
  children?: (item: Item, schema: ISchema<Item>[], renderRow: IRenderRow<Item>) => ReactElement;
  className?: string;
  rowHeightSize?: string;
  itemKey?: string;
  items: Item[];
  placeholder?: string;
  schema: ISchema<Item>[];
}

declare class Table<Item> extends PureComponent<ITableProps<Item>> {}

export default Table;
